// src/pages/OrderComplete.js
import React from 'react';
import { useLocation } from 'react-router-dom';

const OrderComplete = () => {
  const location = useLocation();
  const { orderNumber, cart, totalPrice } = location.state;

  const paymentInfo = `
    입금 계좌: KEB하나은행 123-456-7890
    입금 내용: 주문번호 ${orderNumber}로 입금해 주세요.
  `;

  return (
    <div style={styles.container}>
      <h2>주문 완료</h2>
      <p>주문 번호: {orderNumber}</p>
      <h3>주문 내역</h3>
      <ul>
        {cart.map((item, index) => (
          <li key={index} style={styles.cartItem}>
            <p>{item.name} - {item.quantity}개</p>
            <p>${item.price} x {item.quantity} = ${(item.price * item.quantity).toFixed(2)}</p>
          </li>
        ))}
      </ul>
      <p style={styles.total}>총 가격: ${totalPrice}</p>
      <h3>입금 안내</h3>
      <pre>{paymentInfo}</pre>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '600px',
    margin: '0 auto',
  },
  cartItem: {
    borderBottom: '1px solid #ccc',
    paddingBottom: '10px',
  },
  total: {
    fontWeight: 'bold',
    marginTop: '10px',
    fontSize: '1.2rem',
  },
};

export default OrderComplete;
