// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Signup from './pages/Signup';
import ProductDetail from './pages/ProductDetail'; // 새로 추가한 상세 페이지
import Cart from './pages/Cart';// 장바구니 추가

const App = () => {
  const [cart, setCart] = useState([]);

  const [searchTerm, setSearchTerm] = useState('');

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value); // 검색어 업데이트
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // 검색어에 대한 처리 로직 추가 (예: 필터링)
    console.log('Search term:', searchTerm);
  };

  return (
    <Router>
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '10px' }}>
        <Link to="/">Home</Link>
        <form onSubmit={handleSearchSubmit} style={{ flex: 1, margin: '0 10px' }}>
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            style={{ width: '100%', padding: '5px' }}
          />
        </form>
        <div>
          <Link to="/cart" style={{ marginRight: '10px' }}>Cart ({cart.length})</Link>
          <Link to="/login" style={{ marginRight: '10px' }}>Login</Link>
          <Link to="/signup">Sign Up</Link>
        </div>
      </div>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/product/:id" element={<ProductDetail addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cart={cart} />} />
      </Routes>
    </Router>
  );
};

export default App;
