// src/pages/Cart.js
import React, { useState } from 'react';

const Cart = ({ cart, setCart }) => {
  const handleIncreaseQuantity = (index) => {
    const newCart = [...cart];
    newCart[index].quantity += 1; // 수량 증가
    setCart(newCart); // 상태 업데이트
  };

  const handleDecreaseQuantity = (index) => {
    const newCart = [...cart];
    if (newCart[index].quantity > 1) {
      newCart[index].quantity -= 1; // 수량 감소
    }
    setCart(newCart); // 상태 업데이트
  };

  const handlePurchase = () => {
    // 구매 처리 로직 추가
    console.log('구매 진행:', cart);
    // 결제 처리 로직 등을 추가할 수 있습니다.
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  const calculateTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cart.map((item, index) => (
            <div key={index} style={styles.cartItem}>
              <img src={item.imageUrl} alt={item.name} style={styles.image} />
              <div style={styles.itemDetails}>
                <p style={styles.itemName}>{item.name}</p>
                <p style={styles.itemPrice}>${item.price}</p>
                <div style={styles.quantityContainer}>
                  <button 
                    style={styles.quantityButton} 
                    onClick={() => handleDecreaseQuantity(index)}>-</button>
                  <span style={styles.quantity}>{item.quantity}</span>
                  <button 
                    style={styles.quantityButton} 
                    onClick={() => handleIncreaseQuantity(index)}>+</button>
                </div>
              </div>
            </div>
          ))}
          <div style={styles.total}>
            <p>Total Items: {calculateTotalItems()}</p>
            <p>Total Price: ${calculateTotal()}</p>
          </div>
          <button style={styles.button} onClick={handlePurchase}>구매</button>
        </div>
      )}
    </div>
  );
};

const styles = {
  cartItem: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
    borderBottom: '1px solid #ccc',
    paddingBottom: '10px',
  },
  itemDetails: {
    display: 'flex',
    flexDirection: 'column',
    marginLeft: '15px',
  },
  itemName: {
    fontWeight: 'bold',
    color: 'black',  // 노란색
  },
  itemPrice: {
    color: 'black', // 노란색
    fontSize: '1rem',
  },
  quantityContainer: {
    display: 'flex',
    alignItems: 'center',
    marginTop: '5px',
  },
  quantityButton: {
    padding: '5px 10px',
    margin: '0 5px',
    borderRadius: '5px',
    backgroundColor: '#F2AF38',
    border: 'none',
    cursor: 'pointer',
    color: 'black',
  },
  quantity: {
    fontSize: '1rem',
    margin: '0 5px',
    color: 'black',  // 노란색
  },
  total: {
    marginTop: '20px',
    fontWeight: 'bold',
    color: 'black',  // 노란색
  },
  button: {
    marginTop: '20px',
    padding: '10px 20px',
    border: 'none',
    backgroundColor: '#007bff',
    color: 'white',
    borderRadius: '25px',
    cursor: 'pointer',
    width: '100%',
  },
  image: {
    width: '70px',
    height: '70px',
    marginRight: '15px',
  },
};

export default Cart;
